INSERT INTO product (id, name, description, price, quantity, language, publish_date, author, publisher, image, category_id)
  VALUES (1, 'Giết con chim nhại', 'Một tiểu thuyết kinh điển về nạn phân biệt chủng tộc ở miền Nam nước Mỹ, kể qua góc nhìn của cô bé Scout Finch khi chứng kiến cha mình, luật sư Atticus Finch, bảo vệ một người da đen bị buộc tội oan.', 120000, 50, 'Tiếng Việt', '1960-07-11', 'Harper Lee', 'J.B. Lippincott & Co.', 'https://i.pinimg.com/474x/0f/95/f0/0f95f0cb72dd1eee83856075423016c9.jpg', 1),
         (2, '1984', 'Một tác phẩm dystopia nổi tiếng của George Orwell, mô tả một xã hội toàn trị, nơi chính phủ kiểm soát toàn bộ đời sống của người dân thông qua Big Brother và các biện pháp giám sát nghiêm ngặt.', 135000, 40, 'Tiếng Việt', '1949-06-08', 'George Orwell', 'Secker & Warburg', 'https://i.pinimg.com/474x/43/75/b7/4375b7d9bf24b88aa53744b417227485.jpg', 1),
         (3, 'Nhà giả kim', 'Cuốn sách đầy cảm hứng của Paulo Coelho kể về hành trình tìm kiếm giấc mơ của cậu bé chăn cừu Santiago, mang đến thông điệp sâu sắc về định mệnh, sự kiên trì và ý nghĩa cuộc sống.', 99000, 60, 'Tiếng Việt', '1988-01-01', 'Paulo Coelho', 'HarperCollins', 'https://i.pinimg.com/474x/d6/fe/7a/d6fe7a345fae0de4a2d9e86d1a181c62.jpg', 1),
         (4, 'Chiến tranh và hòa bình', 'Một trong những tiểu thuyết vĩ đại nhất của nhân loại, mô tả cuộc chiến giữa Nga và Pháp thời Napoléon, đồng thời khắc họa sâu sắc số phận của nhiều nhân vật trong xã hội.', 250000, 30, 'Tiếng Việt', '1869-01-01', 'Lev Tolstoy', 'The Russian Messenger', 'https://i.pinimg.com/474x/b3/18/36/b31836d9263d45a4910e8c84ee12c7ba.jpg', 3),
         (5, 'Đắc nhân tâm', 'Cuốn sách kỹ năng sống nổi tiếng của Dale Carnegie giúp con người thành công trong giao tiếp, tạo dựng mối quan hệ và đạt được thành công trong cuộc sống.', 85000, 100, 'Tiếng Việt', '1936-01-01', 'Dale Carnegie', 'Simon & Schuster', 'https://i.pinimg.com/474x/1c/22/df/1c22df7132ad8f1358688b23831e9eaf.jpg', 4),
         (6, 'Bố già', 'Tiểu thuyết về giới mafia nước Mỹ, xoay quanh gia đình Corleone và người đứng đầu Vito Corleone, mang đến một câu chuyện kịch tính về quyền lực, lòng trung thành và sự phản bội.', 150000, 70, 'Tiếng Việt', '1969-03-10', 'Mario Puzo', 'G.P. Putnam’s Sons', 'https://i.pinimg.com/474x/3a/2d/34/3a2d34f0a80d0a462ed5b953df963a3e.jpg', 5),
         (7, 'Những kẻ khốn cùng', 'Một kiệt tác của Victor Hugo, kể về số phận của Jean Valjean – một người tù vượt lên số phận để trở thành con người lương thiện, trong bối cảnh lịch sử nước Pháp đầy biến động.', 200000, 40, 'Tiếng Việt', '1862-01-01', 'Victor Hugo', 'A. Lacroix, Verboeckhoven & Cie.', 'https://i.pinimg.com/474x/19/9c/85/199c85739f0624bed98b1aa2091a1dd8.jpg', 1),
         (8, 'Hoàng tử bé', 'Một tác phẩm kinh điển của Antoine de Saint-Exupéry, mang đến triết lý sâu sắc về cuộc sống thông qua cuộc gặp gỡ giữa một phi công và một cậu hoàng tử từ hành tinh xa xôi.', 75000, 80, 'Tiếng Việt', '1943-04-06', 'Antoine de Saint-Exupéry', 'Reynal & Hitchcock', 'https://i.pinimg.com/474x/73/fe/f2/73fef2d17b9f311e713bee4bcba584d7.jpg', 1),
         (9, 'Harry Potter và Hòn đá phù thủy', 'Phần đầu tiên trong loạt truyện huyền thoại của J.K. Rowling, theo chân cậu bé Harry Potter khám phá thế giới phép thuật tại trường Hogwarts.', 180000, 120, 'Tiếng Việt', '1997-06-26', 'J.K. Rowling', 'Bloomsbury', 'https://i.pinimg.com/474x/54/57/06/545706e454d89e3b5d1e92a03219faa8.jpg', 10),
         (10, 'Chúa tể những chiếc nhẫn', 'Bộ tiểu thuyết sử thi nổi tiếng của J.R.R. Tolkien về hành trình tiêu diệt chiếc Nhẫn Chúa của Frodo Baggins cùng những người bạn đồng hành.', 300000, 50, 'Tiếng Việt', '1954-07-29', 'J.R.R. Tolkien', 'Allen & Unwin', 'https://i.pinimg.com/474x/6e/48/8d/6e488d8de0ef06d416153a17a2219030.jpg', 3),
         (11, 'Dune: Hành tinh cát', 'Một tác phẩm khoa học viễn tưởng kinh điển, kể về cuộc chiến giành quyền kiểm soát hành tinh sa mạc Arrakis và loại gia vị quý giá nhất vũ trụ.', 220000, 45, 'Tiếng Việt', '1965-08-01', 'Frank Herbert', 'Chilton Books', 'https://i.pinimg.com/474x/7c/22/1a/7c221ad09dab084d1a5a7d0488426e1d.jpg', 3),
         (12, 'Cuốn theo chiều gió', 'Tiểu thuyết lãng mạn lấy bối cảnh Nội chiến Hoa Kỳ, xoay quanh cuộc đời đầy biến động của Scarlett O’Hara.', 180000, 35, 'Tiếng Việt', '1936-06-30', 'Margaret Mitchell', 'Macmillan Publishers', 'https://i.pinimg.com/474x/9b/53/63/9b536321567f892a6e3eec437c5d84e6.jpg', 2),
         (13, 'Trăm năm cô đơn', 'Câu chuyện về bảy thế hệ của gia đình Buendía tại Macondo, phản ánh hiện thực huyền ảo của văn học Mỹ Latinh.', 160000, 55, 'Tiếng Việt', '1967-05-30', 'Gabriel García Márquez', 'Editorial Sudamericana', 'https://i.pinimg.com/474x/da/d8/3c/dad83c52636724b29e6055b457bc197a.jpg', 8),
         (14, 'Lược sử thời gian', 'Một cuốn sách khoa học phổ thông nổi tiếng của Stephen Hawking, giải thích các khái niệm vũ trụ học một cách dễ hiểu.', 140000, 65, 'Tiếng Việt', '1988-04-01', 'Stephen Hawking', 'Bantam Books', 'https://i.pinimg.com/474x/98/3d/ba/983dbaa53413296e295741cb081a3c51.jpg', 1),
         (15, 'Nhà thờ Đức Bà Paris', 'Tiểu thuyết lãng mạn lịch sử về cuộc đời bi kịch của Quasimodo – gã gù làm nghề kéo chuông tại nhà thờ Notre-Dame.', 175000, 50, 'Tiếng Việt', '1831-03-16', 'Victor Hugo', 'Gosselin', 'https://i.pinimg.com/474x/76/14/f1/7614f1ccd012fcfbfb039241ca6a1cb7.jpg', 9),
         (16, 'Tội ác và trừng phạt', 'Một tác phẩm triết học của Dostoevsky về tội lỗi, lương tâm và sự chuộc lỗi thông qua nhân vật chính Raskolnikov.', 190000, 40, 'Tiếng Việt', '1866-01-01', 'Fyodor Dostoevsky', 'The Russian Messenger', 'https://i.pinimg.com/474x/cb/f4/83/cbf4830f2a0b0916952beea59d854ad0.jpg', 1),
         (17, 'Đồi gió hú', 'Tiểu thuyết kinh điển về tình yêu mãnh liệt nhưng đầy đau khổ của Heathcliff và Catherine.', 130000, 75, 'Tiếng Việt', '1847-12-01', 'Emily Brontë', 'Thomas Cautley Newby', 'https://i.pinimg.com/474x/6e/af/e2/6eafe21448e43ca5b2053d4d19dc48ab.jpg', 1),
         (18, 'Frankenstein', 'Tác phẩm tiên phong của thể loại khoa học viễn tưởng, kể về con quái vật do tiến sĩ Frankenstein tạo ra.', 110000, 85, 'Tiếng Việt', '1818-01-01', 'Mary Shelley', 'Lackington, Hughes, Harding, Mavor & Jones', 'https://i.pinimg.com/474x/c5/ca/d9/c5cad9f107d0b9be206ddbe0a6136d6c.jpg', 2),
         (19, 'Tiếng gọi nơi hoang dã', 'Cuộc hành trình phiêu lưu của chú chó Buck từ cuộc sống yên bình đến nơi hoang dã khắc nghiệt.', 95000, 90, 'Tiếng Việt', '1903-01-01', 'Jack London', 'Macmillan Publishers', 'https://i.pinimg.com/474x/63/e9/cd/63e9cd', 8),
         (20, 'Don Quixote', 'Cuốn tiểu thuyết Tây Ban Nha huyền thoại về hiệp sĩ Don Quixote và người hầu trung thành Sancho Panza.', 210000, 60, 'Tiếng Việt', '1605-01-16', 'Miguel de Cervantes', 'Francisco de Robles', 'https://i.pinimg.com/474x/f5/f9/90/f5f990077fcd48579e973abd36daa463.jpg', 1),
         (21, 'Bá tước Monte Cristo', 'Tiểu thuyết kinh điển về báo thù và tha thứ, kể về hành trình của Edmond Dantès từ một tù nhân trở thành bá tước quyền lực.', 195000, 45, 'Tiếng Việt', '1844-08-28', 'Alexandre Dumas', 'Penguin Classics', 'https://i.pinimg.com/474x/49/a1/88/49a188302c80e6f13fd5993641606382.jpg', 1),
         (22, 'Túp lều bác Tom', 'Một tác phẩm quan trọng trong lịch sử nước Mỹ, phản ánh thực trạng chế độ nô lệ thông qua câu chuyện đầy cảm động của bác Tom.', 135000, 55, 'Tiếng Việt', '1852-03-20', 'Harriet Beecher Stowe', 'John P. Jewett & Company', 'https://i.pinimg.com/474x/97/97/d9/9797d959f963d49a196cb2d3c398a715.jpg', 1),
         (23, 'Chuyện hai thành phố', 'Một trong những tiểu thuyết nổi tiếng nhất của Charles Dickens, kể về số phận đan xen giữa London và Paris trong Cách mạng Pháp.', 160000, 60, 'Tiếng Việt', '1859-04-30', 'Charles Dickens', 'Chapman & Hall', 'https://i.pinimg.com/474x/e3/f0/ed/e3f0ed65e93af475fe0d716dee3765d3.jpg', 4),
         (24, 'Đảo giấu vàng', 'Một tác phẩm phiêu lưu kinh điển kể về chuyến hành trình tìm kho báu của Jim Hawkins và cuộc đối đầu với tên cướp biển Long John Silver.', 125000, 70, 'Tiếng Việt', '1883-11-14', 'Robert Louis Stevenson', 'Cassell & Company', 'https://i.pinimg.com/474x/81/6e/06/816e06325d830292aa1c14621f7422ee.jpg', 10),
         (25, 'Cuộc phiêu lưu của Sherlock Holmes', 'Tuyển tập truyện ngắn về thám tử lừng danh Sherlock Holmes và người bạn đồng hành trung thành, bác sĩ Watson.', 140000, 65, 'Tiếng Việt', '1892-10-14', 'Arthur Conan Doyle', 'George Newnes', 'https://i.pinimg.com/474x/81/6e/06/816e06325d830292aa1c14621f7422ee.jpg', 6),
         (26, 'Chúa ruồi', 'Một tác phẩm văn học phản ánh bản chất con người qua câu chuyện của nhóm trẻ em bị lạc trên hoang đảo và dần rơi vào hỗn loạn.', 115000, 75, 'Tiếng Việt', '1954-09-17', 'William Golding', 'Faber and Faber', 'https://i.pinimg.com/474x/f4/cd/98/f4cd98406f0679f874f923e47adf5cd3.jpg', 1),
         (27, 'Lão già và biển cả', 'Một tiểu thuyết ngắn nhưng sâu sắc của Hemingway, kể về cuộc chiến đấu kiên cường giữa ông lão đánh cá Santiago và con cá kiếm khổng lồ.', 99000, 80, 'Tiếng Việt', '1952-09-01', 'Ernest Hemingway', 'Charles Scribner’s Sons', 'https://i.pinimg.com/474x/58/05/33/5805332107eedeb421baa3fcf1d2f6c9.jpg', 1),
         (28, 'Bông hồng vàng', 'Tập truyện ngắn mang đậm chất triết lý và chất thơ của Paustovsky, đưa người đọc vào thế giới của cái đẹp và lòng nhân ái.', 105000, 85, 'Tiếng Việt', '1955-01-01', 'Konstantin Paustovsky', 'Molodaya Gvardiya', 'https://i.pinimg.com/474x/9e/54/7b/9e547bffa4c7086be6847c5dc2cc7cbc.jpg', 7),
         (29, 'Không gia đình', 'Một câu chuyện cảm động về cậu bé Rémi mồ côi, phải lang bạt kiếm sống nhưng vẫn giữ trọn tấm lòng nhân hậu.', 125000, 90, 'Tiếng Việt', '1878-01-01', 'Hector Malot', 'Éditions Hachette', 'https://i.pinimg.com/474x/42/66/87/42668721450f1854c0634e6a765ee821.jpg', 10),
         (30, 'Người vô hình', 'Một cuốn tiểu thuyết khoa học viễn tưởng về một nhà khoa học phát minh ra cách trở nên vô hình nhưng bị ám ảnh bởi quyền lực và bị xã hội ruồng bỏ.', 132000, 50, 'Tiếng Việt', '1897-06-01', 'H.G. Wells', 'Pearson’s Magazine', 'https://i.pinimg.com/474x/4d/9d/c4/4d9dc4315b5abac5059a8402eed82ada.jpg', 2);

